                                    <div className="line"><img src={line} /></div>
                                    <div className="selectPrice selectPrice-ingredients"> $15</div>
                                        <div className="minusBg" onClick={onClickMinus}>
                                            <div className="minus"></div>
                                        </div>
                                        <div className="ingredientNum">{num}</div>
                                        <div className="plusBg">
                                        <div className="minus"></div>
                                            <div className="plus"></div>
                                        </div>
                                </div>
                            </div>
                            <div className="ingredientBox">
                                <div className="ingredients-items">
                                    <div class="iname_yellow">Vegetable</div>
                                    <div className="line"><img src={line} /></div>
                                    <div className="selectPrice selectPrice-ingredients"> $15</div>
                                        <div className="minusBg" onClick={onClickMinus}>
                                            <div className="minus"></div>
                                        </div>
                                        <div className="ingredientNum">{num}</div>
                                        <div className="plusBg">
                                        <div className="minus"></div>
                                            <div className="plus"></div>
                                        </div>
                                </div>
                            </div>
                            <div className="ingredientBox">
                                <div className="ingredients-items">
                                    <div class="iname_yellow">Tomato</div>
                                    <div className="line"><img src={line} /></div>
                                    <div className="selectPrice selectPrice-ingredients"> $15</div>
                                        <div className="minusBg" onClick={onClickMinus}>
                                            <div className="minus"></div>
                                        </div>
                                        <div className="ingredientNum">{num}</div>
                                        <div className="plusBg">
                                        <div className="minus"></div>
                                            <div className="plus"></div>
                                        </div>
                                </div>
                            </div>
                            <div className="ingredientBox">
                                <div className="ingredients-items">
                                    <div class="iname_yellow">Onion</div>
                                    <div className="line"><img src={line} /></div>
                                    <div className="selectPrice selectPrice-ingredients"> $15</div>
                                        <div className="minusBg" onClick={onClickMinus}>
                                            <div className="minus"></div>
                                        </div>
                                        <div className="ingredientNum">{num}</div>
                                        <div className="plusBg">
                                        <div className="minus"></div>
                                            <div className="plus"></div>
                                        </div>
                                </div>
                            </div>
                        </div>
                    </div>
