                type: ADD_INGREDIENT4,
                payload: 1
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 15
            })
        };
        if(state.num4 === 1) {
            dispatch({
                type: ADD_INGREDIENT4,
                payload: 2
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 15
            })
        };
    }
    const onChangeBun = e => {
        console.log('radio checked', e.target.value);
        setValue(e.target.value);
        if(value === materials[0].price) {
            dispatch({
                type: CHANGE_BUNS,
                payload: "classic"
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 10
            });
            dispatch({
                type: ADD_CART_BUN,
                payload: state.cartBun = materials[1].name
            })
        }
        if(value === materials[1].price) {
            dispatch({
                type: CHANGE_BUNS,
                payload: "black"
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total - 10
            });
            dispatch({
                type: ADD_CART_BUN,
                payload: state.cartBun = materials[0].name
            })
        }
    }
    const onChangeMeat = e => {
        console.log('radio2 checked', e.target.value);
        setValueMeat(e.target.value);
        if(valueMeat === materials[2].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "shrimp"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 80
            });
            dispatch({
                type: ADD_CART_MEAT,
                payload: state.cartMeat = materials[3].name
            })

        }
        if(valueMeat === materials[3].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "bacon"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 90
            })
            dispatch({
                type: ADD_CART_MEAT,
                payload: state.cartMeat = materials[4].name
            })
        }
        if(valueMeat === materials[4].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "beef"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 70
            })
            dispatch({
                type: ADD_CART_MEAT,
                payload: state.cartMeat = materials[2].name
            })
        }
    }
