            dispatch({
                type: REMOVE_INGREDIENT1,
                payload: 0
            })
        };
    }
    const  onClickPlus1 = () => {
        if(state.num1 === 0) {
            dispatch({
                type: ADD_INGREDIENT1,
                payload: 1
            })
        };
        if(state.num1 === 1) {
            dispatch({
                type: ADD_INGREDIENT1,
                payload: 2
            })
        };
    }
    const  onClickMinus2 = () => {
        if(state.num2 === 2) {
            dispatch({
