            })
        };
        if(state.num4 === 1) {
            dispatch({
                type: REMOVE_INGREDIENT4,
                payload: 0
            })
        };
    }
    const  onClickPlus4 = () => {
        if(state.num4 === 0) {
            dispatch({
                type: ADD_INGREDIENT4,
                payload: 1
            })
