import { Row, Col } from 'antd';
import { Link, useHistory } from 'react-router-dom';
import React, { useContext } from 'react';
import { Form, Input, Button } from 'antd';
import { UserOutlined, LockOutlined, WarningOutlined, MailFilled } from '@ant-design/icons';
import { StoreContext } from '../store/index';
import { registerToFirebase } from '../actions/index';
import { LOGIN_STATE, SUCCESS_COMMENT, BEGIN_COMMENT } from '../utils/constants';
import leftPhone from '../images/icon-phone.png';
import leftMail from '../images/icon-mail.png';
import leftLocation from '../images/icon-location.png';
import { addComment } from '../api/index';

export default function ContactDetail() {
    const { state: { userSignin: { userInfo } , login, commentsContent: { contentInfo, sendLoading } }, dispatch } = useContext(StoreContext);
    const [form] = Form.useForm();

    // const { email } = userInfo;

    const history = useHistory();

    const commentToFirebase = async (dispatch, contentInfo) => {
        dispatch({
            type: BEGIN_COMMENT
        });
        const para = await addComment(contentInfo.email, contentInfo.content, contentInfo.name);
        console.log(para);
        dispatch({
            type: SUCCESS_COMMENT,
            payload: para.providerData[0]
        })
        return(para)
    }
    const contactFinish = async (values) => {
        console.log('Received value of form: ', values);
        const para = await commentToFirebase(dispatch, values);
        para && history.push("/")
;    }
    return(
        <div>
            <Row>
                <Col span={12} className="contact-left">
                    <div className="contact-left-box">
                        <div className="contact-left-title">Contact us</div>
                        <div className="contact-left-information-box">
                            <Row>
                                <Col span={5} className="contact-left-icon-box contact-leftPhone-box"><img src={leftPhone} className="contact-left-icon" /></Col>
                                <Col span={19} className="contact-left-p-box contact-leftPhone-box"><div className="contact-left-p">0987654321</div></Col>
                            </Row>
                            <Row>
                                <Col span={5} className="contact-left-icon-box"><img src={leftMail} className="contact-left-icon" /></Col>
                                <Col span={19} className="contact-left-p-box"><div className="contact-left-p">burger000@gmail.com</div></Col>
                            </Row>
                            <Row>
                                <Col span={5} className="contact-left-icon-box"><img src={leftLocation} className="contact-left-icon"/></Col>
                                <Col span={19} className="contact-left-p-box"><div className="contact-left-p">123 Burger Street</div></Col>
                            </Row>
                        </div>
                    </div>
                </Col>
                <Col span={12} className="contact-right-box">
                    <Form 
                        form={form}
                        name="contact" 
                        className="contact-form"
                        onFinish={contactFinish}>
                        <Form.Item
                            name="name"
                            rules={[
                            { 
                                required: true,
                                message: "Please input your name",
                            },
                            ]}
                        >
                            <Input prefix={<UserOutlined className="site-form-item-icon contact-user-icon" />} placeholder="NAME" />
                        </Form.Item>
                        {/* {
                            login === true?<div>{email}</div>: */}
                            <Form.Item
                            name="email"
                            rules={[
                            {
                                type: "email",
                                message: "The input is not valid E-mail!",
                            },
                            {
                                required: true,
                                message: "Please input your E-mail!",
                            },
                            ]}
                        >
                            <Input prefix={<MailFilled className="site-form-item-icon" />} placeholder="YOUR EMAIL" />
                        </Form.Item>
                        {/* } */}

                        <Form.Item
                            name="message"
                            rules={[{ required: true, message: 'Please input your password!' }]}
                        >
                            <Input.TextArea placeholder="MESSAGE"/>
                        </Form.Item>
                        <Form.Item shouldUpdate>
                        {/* {loading ? (
                            <Button
                                type="primary"
                                htmlType="submit"
                                loading
                            >
                                Sign up
                            </Button>):(
                            <Button
                                type="primary"
                                htmlType="submit"
                            >
                                Sign up
                            </Button>
                        )} */}
                            <Button>SEND</Button>
                        </Form.Item>
                    </Form>
                </Col>
            </Row>
        </div>
    );
}
