                payload: 2
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 15
            })
        };
    }
    const  onClickMinus4 = () => {
        if(state.num4 === 2) {
            dispatch({
                type: REMOVE_INGREDIENT4,
                payload: 1
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total - 15
            })
        };
        if(state.num4 === 1) {
            dispatch({
                type: REMOVE_INGREDIENT4,
                payload: 0
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total - 15
            })
        };
    }
    const  onClickPlus4 = () => {
        if(state.num4 === 0) {
            dispatch({
                type: ADD_INGREDIENT4,
                payload: 1
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 15
            })
        };
        if(state.num4 === 1) {
            dispatch({
                type: ADD_INGREDIENT4,
                payload: 2
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 15
            })
        };
    }
    const onChangeBun = e => {
        console.log('radio checked', e.target.value);
        setValue(e.target.value);
        if(value === materials[0].price) {
            dispatch({
                type: CHANGE_BUNS,
                payload: "classic"
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total + 10
            })
        }
        if(value === materials[1].price) {
            dispatch({
                type: CHANGE_BUNS,
                payload: "black"
            });
            dispatch({
                type: SET_PAGE_TOTAL,
                payload: state.total = state.total - 10
            })
        }
    }
    const onChangeMeat = e => {
        console.log('radio2 checked', e.target.value);
        setValueMeat(e.target.value);
        if(valueMeat === materials[2].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "shrimp"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 80
            })

        }
        if(valueMeat === materials[3].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "bacon"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 90
            })
        }
        if(valueMeat === materials[4].price) {
            dispatch({
                type: CHANGE_MEAT,
                payload: "beef"
            });
            dispatch({
                type: SET_MEAT_TOTAL,
                payload: 70
            })
        }

    }

